create table biglob (id int, c clob);

declare
  a_clob clob;
  Buffer varchar2(32767) := rpad('aabb',32767,'ccdd');
  Buffer1 varchar2(3) := 'aaa';
  Amount integer := 32767;
  Amount1 integer := 3;
  Position integer;
begin

  for j in 1 .. 10 loop
  insert into biglob values (j,empty_clob());
  select c into a_clob from biglob where id=j for update;
  for i in 1 .. 100 loop
    Position := dbms_lob.getlength(a_clob)+1;
    dbms_lob.write(a_clob,Amount,Position,Buffer);
  end loop;
  Position := dbms_lob.getlength(a_clob)+1;
  dbms_lob.write(a_clob,Amount1,Position,Buffer1);
  commit;
  end loop;
end;


