drop table t1 purge;
/
create table t1 (id integer,content clob)	
/
declare
  a_clob clob;
  Buffer varchar2(32767) := rpad('aabb',32767,'ccdd');
  Buffer1 varchar2(62) := 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPTRSQUVWXYZ0123456789';
  Amount integer := 32767;
  Amount1 integer := 62;
  Position integer;
begin
  insert into t1 values (1,empty_clob());
  select content into a_clob from t1 where id=1 for update;
  Position := dbms_lob.getlength(a_clob)+1;
  dbms_lob.write(a_clob,Amount,Position,Buffer);
  Position := dbms_lob.getlength(a_clob)+1;
  dbms_lob.write(a_clob,Amount1,Position,Buffer1);
  commit;
end;
/